/** @format */

import React from 'react';

class Register extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			email: '',
			password: '',
			name: '',
		};
	}

	onNameChange = (event) => {
		this.setState({ name: event.target.value });
	};

	onEmailChange = (event) => {
		this.setState({ email: event.target.value });
	};

	onPasswordChange = (event) => {
		this.setState({ password: event.target.value });
	};

	onSubmitSignIn = () => {
		console.log('submit');
		if (
			this.state.email !== '' &&
			this.state.password !== '' &&
			this.state.name !== ''
		) {
			fetch('https://floating-coast-41315.herokuapp.com/register', {
				method: 'post',
				headers: { 'Content-Type': 'application/json' },
				body: JSON.stringify({
					email: this.state.email,
					password: this.state.password,
					name: this.state.name,
				}),
			})
				.then((response) => response.json())
				.then((user) => {
					if (user.id) {
						this.props.loadUser(user);
						this.props.onRouteChange('home');
					} else {
						this.props.getErrorMessage(user);
					}
				});
		}
	};

	render() {
		console.log(this.props.errorMessage);
		return (
			<article className='br3 ba b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center'>
				<main className='pa4 black-80'>
					<div className='measure'>
						<fieldset id='sign_up' className='ba b--transparent ph0 mh0'>
							<legend className='f1 fw6 ph0 mh0'>Register</legend>
							{this.props.errorMessage ? (
								<p>User name exists.Try another one</p>
							) : null}
							<div className='mt3'>
								<label className='db fw6 lh-copy f6 white' htmlFor='name'>
									Name
								</label>
								<input
									className='pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100'
									type='text'
									name='name'
									id='name'
									onChange={this.onNameChange}
								/>
							</div>
							<div className='mt3'>
								<label
									className='db fw6 lh-copy f6 white'
									htmlFor='email-address'>
									Email
								</label>
								<input
									className='pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100'
									type='email'
									name='email-address'
									id='email-address'
									onChange={this.onEmailChange}
								/>
							</div>
							<div className='mv3'>
								<label className='db fw6 lh-copy f6 white' htmlFor='password'>
									Password
								</label>
								<input
									className='b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100'
									type='password'
									name='password'
									id='password'
									onChange={this.onPasswordChange}
								/>
							</div>
						</fieldset>
						<div className=''>
							<input
								onClick={this.onSubmitSignIn}
								className='b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib'
								type='submit'
								value='Register'
							/>
						</div>
					</div>
				</main>
			</article>
		);
	}
}

export default Register;
